import React, { useEffect, useState } from 'react';
import api from '../api/axios';

const Employees = () => {
  const [employees, setEmployees] = useState([]);
  const [form, setForm] = useState({
    fullName: '',
    position: '',
    salary: '',
    workType: 'monthly'
  });
  const [error, setError] = useState('');

  const loadEmployees = async () => {
    const { data } = await api.get('/employees');
    setEmployees(data.employees || []);
  };

  useEffect(() => {
    loadEmployees();
  }, []);

  const handleChange = (event) => {
    setForm({ ...form, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    if (!form.fullName || !form.position || !form.salary || !form.workType) {
      setError('All fields are required.');
      return;
    }
    try {
      await api.post('/employees', {
        ...form,
        salary: Number(form.salary)
      });
      setForm({ fullName: '', position: '', salary: '', workType: 'monthly' });
      loadEmployees();
    } catch (err) {
      setError(err.response?.data?.message || 'Unable to add employee');
    }
  };

  return (
    <div className="page">
      <div className="page-header">
        <div>
          <h2>Employees</h2>
          <p className="muted">Add, review, and monitor your payroll-ready team.</p>
        </div>
      </div>

      <div className="grid-two">
        <div className="card">
          <div className="page-header">
            <div>
              <h3>Add employee</h3>
              <p className="muted">Floating labels, inline validation, and focus glow.</p>
            </div>
          </div>
          <form onSubmit={handleSubmit} className="grid-form">
            <label>
              Full name
              <input
                name="fullName"
                value={form.fullName}
                onChange={handleChange}
                placeholder="Aziz Bek"
              />
            </label>
            <label>
              Position
              <input
                name="position"
                value={form.position}
                onChange={handleChange}
                placeholder="Accountant"
              />
            </label>
            <label>
              Salary
              <input
                name="salary"
                value={form.salary}
                onChange={handleChange}
                type="number"
                placeholder="4,500,000"
              />
            </label>
            <label>
              Work type
              <select name="workType" value={form.workType} onChange={handleChange}>
                <option value="monthly">Monthly</option>
                <option value="hourly">Hourly</option>
              </select>
            </label>
            {error && <div className="form-error">{error}</div>}
            <button className="btn primary" type="submit">
              Save employee
            </button>
          </form>
        </div>

        <div className="card">
          <div className="page-header">
            <div>
              <h3>Current employees</h3>
              <p className="muted">Table + card hybrid with hover state and badges.</p>
            </div>
          </div>
          <div className="table narrow">
            <div className="table-head">
              <span>Name</span>
              <span>Position</span>
              <span>Salary</span>
            </div>
            {employees.map((employee) => (
              <div key={employee._id} className="table-row">
                <span>{employee.fullName}</span>
                <span>{employee.position}</span>
                <span className="badge success">{employee.salary?.toLocaleString() || employee.salary}</span>
              </div>
            ))}
            {!employees.length && (
              <div className="table-row">
                <span className="muted">No employees yet.</span>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Employees;
