    btn_run_payroll: 'Ish haqi hisoblash',
    btn_add_employee: "Xodim qo'shish",
    btn_create_salary_batch: "Ish haqi partiyasini yaratish",
    btn_review_approvals: "Tasdiqlarni ko'rib chiqish",
    btn_export_pdf: 'PDF chiqarish',
    btn_generate_report: 'Hisobot yaratish',
    btn_download_summary: "Xulosa yuklab olish",
    btn_calculate_salary: 'Ish haqini hisoblash',
