    btn_save_changes: "O'zgarishlarni saqlash",
    btn_change: "O'zgartirish",
    btn_close: 'Yopish',
    btn_update_password: 'Parolni yangilash',
    btn_enable: 'Yoqish',
    btn_disable: "O'chirish",
    btn_sign_out: 'Chiqish',
    menu_company_settings: 'Kompaniya sozlamalari',
    menu_view_reports: "Hisobotlarni ko'rish",
    menu_notifications: 'Bildirishnomalar',
    menu_payroll_ready: 'Ish haqi tayyor',
    menu_report_exported: 'Hisobot chiqarildi'
  }
};

const LanguageContext = createContext(null);

export const LanguageProvider = ({ children }) => {
  const [lang, setLang] = useState(localStorage.getItem('hisobchi_lang') || 'en');

  useEffect(() => {
    localStorage.setItem('hisobchi_lang', lang);
    document.documentElement.setAttribute('lang', lang);
  }, [lang]);

  const t = useMemo(
    () => (key) => dictionary[lang]?.[key] || dictionary.en[key] || key,
    [lang]
  );

  const toggleLanguage = () => {
    setLang((prev) => (prev === 'en' ? 'uz' : 'en'));
  };

  return (
    <LanguageContext.Provider value={{ lang, t, toggleLanguage }}>
      {children}
    </LanguageContext.Provider>
  );
};

export const useLanguage = () => useContext(LanguageContext);
