import React, { useEffect, useState } from 'react';
import api from '../api/axios';

const Reports = () => {
  const [summary, setSummary] = useState(null);
  const [form, setForm] = useState({ month: '' });
  const [error, setError] = useState('');

  const loadSummary = async (month) => {
    const query = month ? `?month=${month}` : '';
    const { data } = await api.get(`/salary/report${query}`);
    setSummary(data);
  };

  useEffect(() => {
    loadSummary();
  }, []);

  const handleChange = (event) => {
    setForm({ ...form, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    try {
      await loadSummary(form.month ? Number(form.month) : null);
    } catch (err) {
      setError(err.response?.data?.message || 'Report generation failed');
    }
  };

  return (
    <div className="page">
      <div className="page-header">
        <div>
          <h2>Reports</h2>
          <p className="muted">Crisp analytics for payroll totals by month.</p>
        </div>
      </div>

      <div className="grid-two">
        <div className="card">
          <div className="page-header">
            <div>
              <h3>Run report</h3>
              <p className="muted">Filter by month or leave empty for all periods.</p>
            </div>
          </div>
          <form onSubmit={handleSubmit} className="grid-form">
            <label>
              Month (optional)
              <input
                name="month"
                type="number"
                value={form.month}
                onChange={handleChange}
                placeholder="7"
              />
            </label>
            {error && <div className="form-error">{error}</div>}
            <button className="btn primary" type="submit">
              Generate
            </button>
          </form>
        </div>

        <div className="card">
          <div className="page-header">
            <div>
              <h3>Summary</h3>
              <p className="muted">Clean table with sticky headers on scroll.</p>
            </div>
          </div>
          {summary ? (
            <div className="table">
              <div className="table-head">
                <span>Total payroll</span>
                <span>Records</span>
                <span>Paid</span>
              </div>
              <div className="table-row">
                <span className="strong">{summary.totalPayroll}</span>
                <span>{summary.recordCount}</span>
                <span className="badge success">{summary.paidCount} paid</span>
              </div>
            </div>
          ) : (
            <div className="muted">No report data yet.</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Reports;
