import React, { useEffect, useState } from 'react';
import api from '../api/axios';

const SalaryCalculation = () => {
  const [employees, setEmployees] = useState([]);
  const [form, setForm] = useState({
    employeeId: '',
    month: '',
    bonus: '',
    penalty: ''
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    api.get('/employees').then((res) => setEmployees(res.data.employees || []));
  }, []);

  const handleChange = (event) => {
    setForm({ ...form, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    setMessage('');
    if (!form.employeeId || !form.month) {
      setError('Employee and month are required.');
      return;
    }
    try {
      const payload = {
        employeeId: form.employeeId,
        month: Number(form.month),
        bonus: Number(form.bonus || 0),
        penalty: Number(form.penalty || 0)
      };
      await api.post('/salary/calculate', payload);
      setMessage('Salary calculated and saved.');
      setForm({ employeeId: '', month: '', bonus: '', penalty: '' });
    } catch (err) {
      setError(err.response?.data?.message || 'Calculation failed');
    }
  };

  return (
    <div className="page">
      <div className="page-header">
        <div>
          <h2>Salary calculation</h2>
          <p className="muted">Guide each payroll run with confidence and inline checks.</p>
        </div>
      </div>

      <div className="grid-two">
        <div className="card">
          <div className="page-header">
            <div>
              <h3>Run calculation</h3>
              <p className="muted">Add bonuses, penalties, and lock-in the month.</p>
            </div>
          </div>
          <form onSubmit={handleSubmit} className="grid-form">
            <label>
              Employee
              <select name="employeeId" value={form.employeeId} onChange={handleChange}>
                <option value="">Select employee</option>
                {employees.map((employee) => (
                  <option key={employee._id} value={employee._id}>
                    {employee.fullName}
                  </option>
                ))}
              </select>
            </label>
            <label>
              Month
              <input
                name="month"
                type="number"
                value={form.month}
                onChange={handleChange}
                placeholder="7"
              />
            </label>
            <label>
              Bonuses
              <input
                name="bonus"
                type="number"
                value={form.bonus}
                onChange={handleChange}
                placeholder="500000"
              />
            </label>
            <label>
              Penalties
              <input
                name="penalty"
                type="number"
                value={form.penalty}
                onChange={handleChange}
                placeholder="0"
              />
            </label>
            {error && <div className="form-error">{error}</div>}
            {message && <div className="form-success">{message}</div>}
            <button className="btn primary" type="submit">
              Calculate
            </button>
          </form>
        </div>

        <div className="card">
          <div className="page-header">
            <div>
              <h3>Guidance</h3>
              <p className="muted">Friendly tips to reduce payroll mistakes.</p>
            </div>
          </div>
          <ul className="muted" style={{ display: 'grid', gap: '0.6rem', paddingLeft: '1rem' }}>
            <li>Use the same month number as your accounting period.</li>
            <li>Keep bonuses and penalties positive; weâ€™ll handle math.</li>
            <li>Review employees list first to ensure active status.</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default SalaryCalculation;
