import React, { useEffect, useState } from 'react';
import api from '../api/axios';

const Reports = () => {
  const [reports, setReports] = useState([]);
  const [form, setForm] = useState({ year: '', month: '' });
  const [error, setError] = useState('');

  const loadReports = async () => {
    const { data } = await api.get('/reports');
    setReports(data.reports || []);
  };

  useEffect(() => {
    loadReports();
  }, []);

  const handleChange = (event) => {
    setForm({ ...form, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    if (!form.year) {
      setError('Year is required.');
      return;
    }
    try {
      await api.post('/reports', {
        year: Number(form.year),
        month: form.month ? Number(form.month) : null
      });
      setForm({ year: '', month: '' });
      loadReports();
    } catch (err) {
      setError(err.response?.data?.message || 'Report generation failed');
    }
  };

  return (
    <div className="page">
      <div className="page-header">
        <div>
          <h2>Reports</h2>
          <p className="muted">Generate and download payroll reports.</p>
        </div>
      </div>

      <div className="grid-two">
        <div className="card">
          <h3>Generate report</h3>
          <form onSubmit={handleSubmit}>
            <label>
              Year
              <input
                name="year"
                type="number"
                value={form.year}
                onChange={handleChange}
                placeholder="2025"
              />
            </label>
            <label>
              Month (optional)
              <input
                name="month"
                type="number"
                value={form.month}
                onChange={handleChange}
                placeholder="7"
              />
            </label>
            {error && <div className="form-error">{error}</div>}
            <button className="btn primary" type="submit">
              Generate
            </button>
          </form>
        </div>

        <div className="card">
          <h3>Report history</h3>
          <div className="table">
            <div className="table-row table-head">
              <span>Period</span>
              <span>Employees</span>
              <span>Total payroll</span>
            </div>
            {reports.map((report) => (
              <div key={report._id} className="table-row">
                <span>{report.month ? `${report.month}/${report.year}` : report.year}</span>
                <span>{report.employeeCount}</span>
                <span>{report.totalPayroll}</span>
              </div>
            ))}
            {!reports.length && <div className="muted">No reports yet.</div>}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Reports;
